(async()=>{let e=document.createElement("div");(await fetch("http://localhost:3000/api/posts").then((e=>e.json())).catch((e=>console.log(e)))).forEach((t=>{let n=document.createElement("div"),c=document.createElement("h1"),a=document.createElement("p"),d=document.createElement("p");n.classList.add("post"),c.innerText=t.title,a.innerText=t.content,d.innerText=t.time.created,n.append(c,a,d),e.appendChild(n)})),e.classList.add("container"),document.body.appendChild(e)})();